import '../../css/pages/buy.css';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';

function EditCar(props) {
  
  const location = useLocation();
  const { car } = location.state;
  const [selectedCar,setCar] = useState(car);
  const photoNum = useState(0);

  const navigate = useNavigate();

  const changeHandler = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    const tempCar = { ...selectedCar};
    tempCar[name] = value;
    setCar(tempCar)
    }


  const handleUpdateClick = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:8080/car/updateCar", selectedCar)
      .then((response) => {

        setCar(response.data);
        navigate("/Admin")
      })
      .catch((error) => {
        console.log(error);
      });
  };



  return (
    <div className="car-edit-box">
      <div className='flex-row'><h1 className = 'center'>EditCar</h1></div>
   
        <img className = 'picture-box' src={selectedCar.carPhotos[photoNum].photoUrl} alt={car.model} />
        <div className = 'flex-row center'>
        <button onClick={deletePhoto}>DELETE</button>
        <button onClick={nextPhoto}>NEXT</button>
        <button onClick={prevPhoto}>PREVIOUS</button>
        </div>

        <div className='flex-row center'>
        IMAGE URL:
        <input className = 'input-container' value={selectedCar.carPhotos[photoNum].photoUrl} name='description' type='description' onChange={changeHandler} required></input>
        </div>
    
        <div className='flex-row center'>
        DESCRIPTION:
        <input className = 'input-container' value={selectedCar.description} name='description' type='description' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row center'>
        MAKE:
        <input className = 'input-container' value={selectedCar.make} name='make' type='make' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row center'>
        MODEL:
        <input className = 'input-container' value={selectedCar.model} name='model' type='model' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row center'>
        YEAR:
        <input className = 'input-container' value={selectedCar.year} name='year' type='year' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row center'>
        MILES:
        <input className = 'input-container' value={selectedCar.miles} name='miles' type='miles' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row center'>
        PRICE:
        <input className = 'input-container' value={selectedCar.price} name='price' type='price' onChange={changeHandler} required></input>
        </div>
        <div>
        <button onClick={handleUpdateClick}>Update</button>
        </div>
    </div>
      



  )
}

export default EditCar;
