import '../../css/pages/buy.css';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';

function EditCar(props) {
  
  const location = useLocation();
  const { car } = location.state;
//   const [car,setCar] = useState([selectedCar]);
  
  const navigate = useNavigate();

  const changeHandler = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    const tempCar = { ...car};
    tempCar[name] = value;
    car=tempCar
    // setCar(tempCar)
    }

  const handleUpdateClick = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:8080/car/updateCar", car)
      .then((response) => {

        props.setCar(response.data);
        navigate("/Admin")
      })
      .catch((error) => {
        console.log(error);
      });
  };



  return (
    <div className="flex-column fill">
      <div>EditCar</div>
      <div className='checkout-box'>
        <img src={car.carPhotos[0].photoUrl} alt={car.model} />
        <div className='flex-row'>
        DESCRIPTION:
        <input value={car.description} name='description' type='description' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row'>
        MAKE:
        <input value={car.make} name='make' type='make' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row'>
        MAKE:
        <input value={car.model} name='model' type='model' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row'>
        YEAR:
        <input value={car.year} name='year' type='year' onChange={changeHandler} required></input>
        </div>
        MILES:
        <input value={car.miles} name='miles' type='miles' onChange={changeHandler} required></input>
        </div>
        <div>
        PRICE:
        <input value={car.price} name='price' type='price' onChange={changeHandler} required></input>
        </div>
        <button onClick={handleUpdateClick}>Update</button>
      </div>



  )
}

export default EditCar;
