import '../../css/pages/buy.css';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';

function EditCar(props) {
  
  const location = useLocation();
  const { car } = location.state;
  const [selectedCar,setCar] = useState(car);
  
  const navigate = useNavigate();

  const changeHandler = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    const tempCar = { ...selectedCar};
    tempCar[name] = value;
    setCar(tempCar)
    }

  const handleUpdateClick = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:8080/car/updateCar", selectedCar)
      .then((response) => {

        setCar(response.data);
        navigate("/Admin")
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const showCarPhotos = (car) => {
    return (
      <div>
        {car.carPhotos.map((photo) => (
          <img src={photo.photoUrl} alt={car.model} key={photo.id} />
        ))}
      </div>
    );
  };


  return (
    <div className="flex-column fill">
      <div>EditCar</div>
      <div className='car-edit-box'>
        <img className = 'picture-box' src={showCarPhotos(selectedCar)} alt={car.model} />
        </div>
        <div className='flex-row'>
        DESCRIPTION:
        <input value={selectedCar.description} name='description' type='description' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row'>
        MAKE:
        <input value={selectedCar.make} name='make' type='make' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row'>
        MAKE:
        <input value={selectedCar.model} name='model' type='model' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row'>
        YEAR:
        <input value={selectedCar.year} name='year' type='year' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row'>
        MILES:
        <input value={selectedCar.miles} name='miles' type='miles' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row'>
        PRICE:
        <input value={selectedCar.price} name='price' type='price' onChange={changeHandler} required></input>
        </div>
        <div className='flex-row'>
        <button onClick={handleUpdateClick}>Update</button>
        </div>
      </div>



  )
}

export default EditCar;
